import { GET_DATA_LOADING, getDataSuccess, getDataFailure } from '../actions';
import { take, put, takeLatest,call } from 'redux-saga/effects';
import { getData } from '../functions/GetData';

function* GetDataSaga() {
    const response = yield call (getData);
    const data = response.data

    if (response.status !== 200) {
        yield take(getDataFailure)
    }

    yield put (getDataSuccess(data));
}

export function* watchGetDataSaga() {
    yield takeLatest(GET_DATA_LOADING, GetDataSaga)
}
