import {
  GET_DATA_SUCCESS,
  GET_DATA_LOADING,
  GET_DATA_FAILURE,
  CREATE_TASK,
} from '../actions';

const initialState = {
  gettingData: false,
  data: [],
};

for (let i = 0; i < 5; i++) {
  initialState.data.push({
    key: i.toString(),
    name: `Task ${i}`,
  });
}

export function TaskReducer(state = [], action) {
  switch (action.type) {
    case CREATE_TASK:
      return [...state, { ...action.task }];
    default:
      return state;
  }
}
