import React from 'react';
import './App.css';
import { HashRouter, Switch, Route, Redirect, NavLink } from 'react-router-dom';

import {
  MailOutlined,
  AppstoreOutlined,
  SettingOutlined,
  UnorderedListOutlined,
  PlusOutlined,
} from '@ant-design/icons';
import InputPage from './screens/InputScreen';
import ListScreen from './screens/ListScreen';
import { Layout, Menu, Breadcrumb } from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined,
  FileOutlined,
  TeamOutlined,
  UserOutlined,
} from '@ant-design/icons';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

function App() {
  const state = {
    collapsed: false,
  };

  const onCollapse = (collapsed) => {
    console.log(collapsed);
    state.collapsed = collapsed ;
  };
  return (
    // <HashRouter>
    //   <header />
    //   <nav>
    //     <div>
    //       <Menu mode="horizontal">
    //         <Menu.Item
    //           className="headerLink"
    //           key="Add Task"
    //           to="/addTask"
    //           icon={<PlusOutlined />}
    //         >
    //           Add Task
    //         </Menu.Item>

    //         <Menu.Item
    //           className="headerLink"
    //           key="Task List"
    //           to="/list"
    //           icon={<UnorderedListOutlined />}
    //         >
    //           Task List
    //         </Menu.Item>
    //       </Menu>
    //     </div>
    //   </nav>
    //   <div className="app">
    //     <Switch>
    //       <Route exact path="/addTask" component={InputPage} />
    //       <Route exact path="/list" component={ListScreen} />
    //     </Switch>
    //   </div>
    // </HashRouter>
    <Layout style={{ minHeight: '100vh' }}>
      <Sider collapsible collapsed={state} onCollapse={onCollapse}>
        <div className="logo" />
        <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline">
          <Menu.Item key="1" icon={<PieChartOutlined />}>
            Option 1
          </Menu.Item>
          <Menu.Item key="2" icon={<DesktopOutlined />}>
            Option 2
          </Menu.Item>
          <SubMenu key="sub1" icon={<UserOutlined />} title="User">
            <Menu.Item key="3">Tom</Menu.Item>
            <Menu.Item key="4">Bill</Menu.Item>
            <Menu.Item key="5">Alex</Menu.Item>
          </SubMenu>
          <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
            <Menu.Item key="6">Team 1</Menu.Item>
            <Menu.Item key="8">Team 2</Menu.Item>
          </SubMenu>
          <Menu.Item key="9" icon={<FileOutlined />} />
        </Menu>
      </Sider>
      <Layout className="site-layout">
        <Header className="site-layout-background" style={{ padding: 0 }} />
        <Content style={{ margin: '0 16px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>User</Breadcrumb.Item>
            <Breadcrumb.Item>Bill</Breadcrumb.Item>
          </Breadcrumb>
          <div
            className="site-layout-background"
            style={{ padding: 24, minHeight: 360 }}
          >
            Bill is a cat.
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>
          Ant Design ©2018 Created by Ant UED
        </Footer>
      </Layout>
    </Layout>
  );
}

export default App;
