export const CREATE_TASK = "CREATE_TASK";

export const createTask = (task) => ({
    type: CREATE_TASK,
    task
});

export const EDIT_TASK = "EDIT_TASK";

export const editTask = (task) => ({
    type: editTask,
    task
});
