import { CREATE_TASK } from '../actions';
import { EDIT_TASK } from '../actions/taskActions';

const initialState = [];

for (let i = 0; i < 5; i++) {
  initialState.push({
    key: i.toString(),
    name: `Task ${i}`,
  });
}

export default function taskReducer(state = initialState, action) {
  switch (action.type) {
    case CREATE_TASK:
      return [...state, { ...action.task }];
