import { CREATE_TASK } from '../actions';

const initialState = {
  gettingData: false,
  data: [],
};

for (let i = 0; i < 5; i++) {
  initialState.data.push({
    key: i.toString(),
    name: `Task ${i}`,
  });
}

export default function taskReducer(state = [], action) {
  switch (action.type) {
    case CREATE_TASK:
