export const mergedColumns = columns.map((col) => {
  if (!col.editable) {
    return col;
  }

  return {
    ...col,
    onCell: (record) => ({
      record,
      inputType: col.dataIndex === 'age' ? 'number' : 'text',
      dataIndex: col.dataIndex,
      title: col.title,
    }),
  };
});

const columns = [
  {
    title: 'name',
    dataIndex: 'name',
    width: '25%',
    editable: true,
  },
  {
    title: 'operation',
    dataIndex: 'operation',
    render: (text, record) =>
      props.tasks.length >= 1 ? (
        <Popconfirm
          title="Sure to delete?"
          onConfirm={() => {
            let value = props.tasks.filter((value) => value.task == record);
            handleDelete(value[0].id);
          }}
        >
          <a>Delete</a>
        </Popconfirm>
      ) : null,
  },
];
