import { take, put, takeLatest,call } from 'redux-saga/effects';
import {
  REMOVE_DATA_LOADING,
  removeDataSuccess,
  removeDataFailure,
} from '../actions';
import { getData } from '../functions/GetData';

function* RemoveDataSaga(key) {
  try {
    let response = yield call(getData);
    var data = response.data;
    data = data.filter((item) => item.key !== key);
  } catch (error) {
    yield take(removeDataFailure);
  }
  yield put(removeDataSuccess(data));
}

export function* watchRemoveDataSaga() {
  yield takeLatest(REMOVE_DATA_LOADING, RemoveDataSaga);
}
