import { combineReducers } from 'redux';
import { TaskReducer } from './TaskReducer';


const rootReducer = combineReducers({
  tasks: GetDataReducer,
  setData: SetDataReducer,
  removeData: RemoveDataReducer,
});

export default rootReducer;
