import React from 'react';
import './App.css';
import { HashRouter, Switch, Route, Redirect, NavLink } from 'react-router-dom';
import { Menu } from 'antd';
import {
  MailOutlined,
  AppstoreOutlined,
  SettingOutlined,
  UnorderedListOutlined,
  PlusOutlined,
} from '@ant-design/icons';
import InputPage from './screens/ListPage';

const { SubMenu } = Menu;
const links = ['Input', 'Task List'];
function App() {
  return (
    <HashRouter>
      <header />
      <nav>
        <div>
          <Menu mode="horizontal">
            <Menu.Item
              className="headerLink"
              key="Add Task"
              to="/addTask"
              icon={<PlusOutlined />}
            >
              Add Task
            </Menu.Item>

            <Menu.Item
              className="headerLink"
              key="Task List"
              to="Task List"
              icon={<UnorderedListOutlined />}
            >
              Task List
            </Menu.Item>
          </Menu>
        </div>
      </nav>
      <div className="app">
        <Switch>
          <Route exact path="/addTask" component={InputPage} />
          <Route render={() => <Redirect to="/addTask" />} />
        </Switch>
      </div>
    </HashRouter>
  );
}

export default App;
