import React from 'react';
import './App.css';
import { Input, Button, Form } from 'antd';
import { connect } from 'react-redux';

function App() {
  const [form] = Form.useForm();
  const [input, setInput] = useState({
    title: '',
    description: '',
  });
  const [storage, setStorage] = useState({
    title: '',
    description: '',
  });

  useEffect(() => {
    setData();
  }, []);

  const handleInputChange = (event) => {
    event.preventDefault();
    const {
      target: { name, value },
    } = event;
    setInput({
      ...input,
      [name]: value,
    });
  };
  const handleSubmit = (event) => {
    event.preventDefault();
    setStorage({
      title: input.title,
      description: input.description,
    });
    Data.push(storage);
    setInput({
      title: '',
      description: '',
    });
  };
  return (
    <div className="App">
      <Form form={form}>
        <div className="form">
          <Form.Item>
            <Input
              name="title"
              className="input"
              placeholder="input title"
            />
          </Form.Item>

          <Form.Item>
            <Input
              name="description"
              className="input"
              placeholder="input description"
            />
          </Form.Item>

          <Form.Item>
            <Button className="button" type="primary">
              Submit
            </Button>
          </Form.Item>
        </div>
      </Form>
    </div>
  );
}

export default App;
