import { take, put, call, takeLatest } from 'redux-saga/effects'
import { getUsers } from "../api/Api";
import { GET_DATA_LOADING } from '../actions/GetDataActions';


function* getUsersSaga() {
    const response = yield call(getUsers);
    // function getFields(input, field1) {
    //     let output = [];
    //     for (let i=0; i < input.length ; ++i)
    //         output.push(input[i][field1]);
    //     return output;
    // }
    // console.log(response.data);
    
    const data = response.data;
    if (response.status !== 200) {
        yield take(getUsersFailure)
    }
    yield put(getUsersSuccess(data));
}

export function* watchGetDataSaga() {
    yield takeLatest(GET_DATA_LOADING, GetDataSaga)
}
