import React from 'react';
import './App.css';
import { HashRouter, Switch, Route, Redirect, NavLink } from 'react-router-dom';
import { Menu, } from 'antd';
import {
  UnorderedListOutlined,
  PlusOutlined,
} from '@ant-design/icons';
import InputPage from './screens/InputScreen';
import ListScreen from './screens/ListScreen';
import { Breadcrumb } from 'antd';

const { SubMenu } = Menu;
const links = ['Input', 'Task List'];
function App() {
  return (
    <HashRouter>
      <header />
      <nav>
        <div>
          <Breadcrumb mode="horizontal" onClick= {()=>{<Redirect to='/' />}}>
            <Menu.Item
              className="headerLink"
              key="Add Task"
              to="/addTask"
              icon={<PlusOutlined />}
            >
              Add Task
            </Menu.Item>

            <Menu.Item
              className="headerLink"
              key="Task List"
              to="/list"
              icon={<UnorderedListOutlined />}
            >
              Task List
            </Menu.Item>
          </Breadcrumb>
        </div>
      </nav>
      <div className="app">
        <Switch>
          <Route exact path="/addTask" component={InputPage} />
          <Route exact path="/list" component={ListScreen} />
        </Switch>
      </div>
    </HashRouter>
  );
}

export default App;
