import { take, put, call, takeLatest } from 'redux-saga/effects';
import { getTasks, addTask } from '../api/TaskApi';
import { SET_DATA_LOADING, setDataFailure, setDataSuccess, REMOVE_DATA_LOADING } from '../actions';
import { task } from '../components/addTask/AddingTaskPage';

function* RemoveDataSaga() {
  if (task !== undefined && task !== null) {
    const response = yield ;
    const data = response.data;
    yield put(removeDataSuccess(data));
  }
}

export function* watchSetDataSaga() {
  yield takeLatest(REMOVE_DATA_LOADING, RemoveDataSaga);
}
