import React from 'react';
import './App.css';
import { Button, DatePicker, version } from 'antd';

function App() {
  const [form] = Form.useForm();
  const [input, setInput] = useState({
      title: '',
      description: '',
  })
  const [storage, setStorage] = useState({
      title: '',
      description: '',
  })

  useEffect(() => {
      setData();
  }, [])

  const handleInputChange = (event) => {
      event.preventDefault();
      const {
          target: { name, value }
      } = event;
      setInput({
          ...input,
          [name]: value
      });
  }
  const handleSubmit = (event) => {
      event.preventDefault();
      setStorage({
          title: input.title,
          description: input.description,
      });
      Data.push(storage);
      setInput({
          title: "",
          description: "",
      });
  }
  return (
    <div className="App">
      <h1>antd version: {version}</h1>
      <Form
            form={form}

        >
            <div className='form'>

                <Form.Item >
                    <Input onChange={handleInputChange} value={input.title} name='title' className='input' placeholder="input title" />
                </Form.Item>

                <Form.Item >
                    <Input onChange={handleInputChange} value={input.description} name='description' className='input' placeholder="input description" />
                </Form.Item>

                <Form.Item >
                    <Button onClick={handleSubmit} className='button' type="primary">Submit</Button>
                </Form.Item>

            </div>
        </Form>
    </div>
  );
}

export default App;
